CUSTOMER = spark.sql("""
SELECT *, 
       if(isnull(Customer1), 'ZZNONE', Customer1) as Customer,
       if(isnull(`Customer_Type_Code1`), 'ZZNONE', `Customer_Type_Code1`) as `Customer_Type_Code`,
       if(isnull(`Customer_Group_Code1`), 'ZZNONE', `Customer_Group_Code1`) as `Customer_Group_Code`,
       if(isnull(`Customer_BEE_Score1`), 'ZZNONE', `Customer_BEE_Score1`) as `Customer_BEE_Score`,
       if(isnull(`Customer_Terms1`), 'ZZNONE', `Customer_Terms1`) as `Customer_Terms`
FROM CUSTOMER
""")
#CUSTOMER.show()
CUSTOMER.createOrReplaceTempView("CUSTOMER")
