sqlReaderQuery.value:
@concat('SELECT MAX(',item().Increment.IncField,')  as Field 
 FROM
    OPENROWSET(
        BULK ','''',variables('DataLake'),variables('ContainerName'),'/',variables('FolderName'),'/',item().destination.FileName,'.parquet','''',
        ',FORMAT = ','''PARQUET''','
    ) AS [result]')

@concat(if(or(pipeline().parameters.ColumnsNamesBlankSpaces, greater(length(pipeline().parameters.LimitedLoadTopX),1)),
        activity('SelectStatementBuilder').output.firstRow.Query,concat('SELECT * FROM [', item().Source.TableName,']')
        )
    ,' WHERE ', item().Increment.IncField, ' > ',
        if(greater(length(pipeline().parameters.TakeOnPeriod),1)
        ,pipeline().parameters.TakeOnPeriod
        ,concat('''',activity('Lookup Last Max Field').output.firstRow.Field,'''')
        )
    )

@concat(if(or(pipeline().parameters.ColumnsNamesBlankSpaces,    greater(length(pipeline().parameters.LimitedLoadTopX),1)),
        activity('SelectStatementBuilder').output.firstRow.Query, concat('SELECT * FROM [', item().Source.TableName,']')
        )   
    ,' WHERE ', item().Increment.IncField, ' > ',
        if(greater(length(pipeline().parameters.TakeOnPeriod),1)
        ,pipeline().parameters.TakeOnPeriod
        ,activity('Lookup Last Max Field').output.firstRow.Field
        )
